'use strict';

/**
 * Base Service containing logic shared by all services
 * @author kvermeer
 */
class BaseService {

    /**
     * Returns a properly formatted success response
     *
     * @param {object} data - data to be sent back to the caller
     * @return {object}
     */
    getSuccessResponse(data) {
        return {
            data: data
        };
    }

    /**
     * Returns a properly formatted error response
     *
     * @param {string} reason - the error reason for failure (i.e. 'USER_NOT_FOUND')
     * @return {object}
     */
    getErrorResponse(reason) {
        return {
            error: {
                reason: reason
            }
        };
    }
}

module.exports = BaseService;
