/**
 * Nugget model
 * Represents a "nugget" of information
 * @author kvermeer
 * @model
 */

// Depedencies
var DatabaseManager = require('core/database');
var sequelize = DatabaseManager.getSequelizeInstance()

/**
 * Nugget definition in sequelize
 */
var Nugget = sequelize.define('nugget', {
    nugget_id: { type: DatabaseManager.Sequelize.BIGINT, primaryKey: true, autoIncrement: true },
    user_id: { type: DatabaseManager.Sequelize.BIGINT },
    content_markup_format: { type: DatabaseManager.Sequelize.STRING },
    content: { type: DatabaseManager.Sequelize.TEXT },
    created_at: { type: DatabaseManager.Sequelize.DATE },
    updated_at: { type: DatabaseManager.Sequelize.DATE }
}, {
    freezeTableName: true,
    tableName: 'nugget',
    underscored: true,
    timestamps: true,
});

module.exports = Nugget;
