/**
 * Tag model
 * Represents a tag, which is just an arbitrary label
 * @author kvermeer
 * @model
 */

// Depedencies
var DatabaseManager = require('core/database');
var sequelize = DatabaseManager.getSequelizeInstance()

/**
 * Nugget definition in sequelize
 */
var Tag = sequelize.define('tag', {
    tag_id: { type: DatabaseManager.Sequelize.BIGINT, primaryKey: true, autoIncrement: true },
    user_id: { type: DatabaseManager.Sequelize.BIGINT },
    tag_name: { type: DatabaseManager.Sequelize.STRING },
    created_at: { type: DatabaseManager.Sequelize.DATE },
    updated_at: { type: DatabaseManager.Sequelize.DATE }
}, {
    freezeTableName: true,
    tableName: 'tag',
    underscored: true,
    timestamps: true,
});

module.exports = Tag;
