/**
 * User model
 * Represents a user of our service
 * @author kvermeer
 * @model
 */

// Depedencies
var DatabaseManager = require('core/database');
var sequelize = DatabaseManager.getSequelizeInstance()

/**
 * Nugget definition in sequelize
 */
var User = sequelize.define('user', {
    user_id: { type: DatabaseManager.Sequelize.BIGINT, primaryKey: true, autoIncrement: true },
    display_name: { type: DatabaseManager.Sequelize.STRING },
    login: { type: DatabaseManager.Sequelize.STRING },
    password_salt: { type: DatabaseManager.Sequelize.INTEGER },
    password_hash: { type: DatabaseManager.Sequelize.STRING },
    created_at: { type: DatabaseManager.Sequelize.DATE },
    updated_at: { type: DatabaseManager.Sequelize.DATE }
}, {
    freezeTableName: true,
    tableName: 'user',
    underscored: true,
    timestamps: true,
});

module.exports = User;
