/**
 * This creates a router for signup
 * @author kvermeer
 */

var express = require('express');
var router = express.Router();

var Authentication = require('middleware').Authentication;

var ControllerModule = require('./controllers')
var tagController = new ControllerModule.TagController();

router.get('/view/:tagId', Authentication.requireAuthentication, tagController.viewTag.bind(tagController));

router.get('/create', Authentication.requireAuthentication, tagController.getCreateTagPage.bind(tagController));
router.post('/create', Authentication.requireAuthentication, tagController.createTagSubmit.bind(tagController));

module.exports = router;
